$('.header__burger').click(function() {
	$('.header__menu').css({
		'display': 'flex',
		'background-color': '#000',
		'padding-bottom' : '50px'
	});
	$('body').addClass('position-fixed');
	$('.menu__link').addClass('menu__link--mobile');
	$('.about-audit__planet1').css('z-index', '-1');
	$('.about-audit__title').css('display', 'none');
});

$('.header__burger').click(function() {
	if( !$('.burger-checkbox').prop('checked') === true ) {
		$('body').removeClass('position-fixed');
		$('.header__menu').css('display', 'none');
		$('.burger-checkbox').prop('checked', false);
		$('.menu__link').removeClass('menu__link--mobile');
		$('.about-audit__planet1').css('z-index', '10');
	}
});



$(window).on('scroll', function() {
	const scrl = $(this).scrollTop();

	$('.about-audit__planet1').css({
		"transform" : `translate(0%, ${scrl / 7}%)`
	});

	$('.about-audit__planet2').css({
		"transform" : `translate(0%, ${scrl / 2.75}%)`
	});

	$('.about-audit__planet3').css({
		"transform" : `translate(0%, -${scrl / 20}%)`
	});
});
const hideScrollUpIcon = () => {
	const icon = $('.scroll-up__icon');
	icon.removeClass('scroll-up__icon--active');
}

const showScrollUpIcon = () => {
	const icon = $('.scroll-up__icon');
	icon.addClass('scroll-up__icon--active');
}

const isIconShows = () => {
	const bodyHeight = $('body').height();
	const currentScroll = $( window ).scrollTop();
	const windowHeight = $( window ).height();
	const footerHeight = $('.footer').height();

	if( currentScroll > 300 ) {
		showScrollUpIcon();
	} else {
		hideScrollUpIcon();
	}

	if( ( bodyHeight - (currentScroll + windowHeight) ) < footerHeight ) {
		hideScrollUpIcon();
	}
}

$(window).on('scroll', isIconShows);
$("a").click(function () {
    var elementClick = $(this).attr("href");
    var destination = $(elementClick).offset().top;

    $('html, body').animate({ scrollTop: destination }, 300);
    console.log(2);
    return false;
});
