$('.header__burger').click(function() {
	$('.header__menu').css({
		'display': 'flex',
		'background-color': '#000',
		'padding-bottom' : '50px'
	});
	$('body').addClass('position-fixed');
	$('.menu__link').addClass('menu__link--mobile');
	$('.about-audit__planet1').css('z-index', '-1');
	$('.about-audit__title').css('display', 'none');
});

$('.header__burger').click(function() {
	if( !$('.burger-checkbox').prop('checked') === true ) {
		$('body').removeClass('position-fixed');
		$('.header__menu').css('display', 'none');
		$('.burger-checkbox').prop('checked', false);
		$('.menu__link').removeClass('menu__link--mobile');
		$('.about-audit__planet1').css('z-index', '10');
	}
});



$(window).on('scroll', function() {
	const scrl = $(this).scrollTop();

	$('.about-audit__planet1').css({
		"transform" : `translate(0%, ${scrl / 7}%)`
	});

	$('.about-audit__planet2').css({
		"transform" : `translate(0%, ${scrl / 3}%)`
	});

	$('.about-audit__planet3').css({
		"transform" : `translate(0%, ${scrl / 25}%)`
	});
});
$(window).on('scroll', function() {
	if( $(window).scrollTop() > 300 ) {
		$('.icon-scroll-up').css({
			'opacity' : '1',
			'z-index' : '9999'
		});
	} else {
		$('.icon-scroll-up').css({
			'opacity' : '0',
			'z-index' : '-9999'
		});
	}
});

$("a").click(function () {
    var elementClick = $(this).attr("href");
    var destination = $(elementClick).offset().top;

    $('html, body').animate({ scrollTop: destination }, 600);
    console.log(2);
    return false;
});